import React, { useState, useEffect, useRef } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './App.css';

// Import components
import Sidebar from './components/Sidebar';
import ControlPanel from './components/ControlPanel';
import AlertsPanel from './components/AlertsPanel';
import GeofencingPanel from './components/GeofencingPanel';
import SettingsPanel from './components/SettingsPanel';

function App() {
  const [status, setStatus] = useState('Connecting...');
  const [isConnected, setIsConnected] = useState(false);
  const [videoUrl, setVideoUrl] = useState('wss://live-video-serbot.onrender.com'); // Default video stream URL
  const [activeTab, setActiveTab] = useState('control');
  const [videoStream, setVideoStream] = useState(null);
  const [isVideoConnected, setIsVideoConnected] = useState(false);
  const videoWs = useRef(null);
  const [alerts, setAlerts] = useState([
    { id: 1, message: 'Alert 1' },
    { id: 2, message: 'Alert 2' }
  ]);
  const [geofenceSettings, setGeofenceSettings] = useState({
    enabled: true,
    radius: 100,
    center: { lat: 40.7128, lng: -74.0060 }
  });
  const ws = useRef(null);

  useEffect(() => {
    // Initialize WebSocket connection for vehicle control
    ws.current = new WebSocket("wss://bot-controll.onrender.com/ws");

    ws.current.onopen = () => {
      setStatus("Connected to backend");
      setIsConnected(true);
      ws.current.send(JSON.stringify({ type: "register", role: "frontend" }));
    };

    ws.current.onclose = () => {
      setStatus("Disconnected");
      setIsConnected(false);
    };

    ws.current.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.type === "status") {
        setStatus("Car status: " + data.state);
      }
    };

    // Initialize WebSocket connection for video stream
    if (videoUrl) {
      initializeVideoStream();
    }

    // Cleanup on unmount
    return () => {
      if (ws.current) {
        ws.current.close();
      }
      if (videoWs.current) {
        videoWs.current.close();
      }
    };
  }, []);

  const initializeVideoStream = () => {
    if (videoWs.current) {
      videoWs.current.close();
    }

    videoWs.current = new WebSocket(videoUrl);
    videoWs.current.binaryType = "arraybuffer";

    videoWs.current.onopen = () => {
      console.log("Connected to video stream");
      setIsVideoConnected(true);
    };

    videoWs.current.onmessage = (event) => {
      const blob = new Blob([event.data], { type: "image/jpeg" });
      const imageUrl = URL.createObjectURL(blob);
      setVideoStream(imageUrl);
    };

    videoWs.current.onclose = () => {
      console.log("Disconnected from video stream");
      setIsVideoConnected(false);
    };

    videoWs.current.onerror = (err) => {
      console.error("Video WebSocket error:", err);
      setIsVideoConnected(false);
    };
  };

  useEffect(() => {
    if (videoUrl && videoUrl !== '') {
      initializeVideoStream();
    }
  }, [videoUrl]);

  const sendCommand = (action) => {
    if (ws.current && ws.current.readyState === WebSocket.OPEN) {
      ws.current.send(JSON.stringify({ type: "command", action }));
      setStatus(`Sent command: ${action}`);
    }
  };

  const handleMouseDown = (action) => {
    if (action !== "stop") {
      sendCommand(action);
    }
  };

  const handleMouseUp = (action) => {
    if (action !== "stop") {
      sendCommand("stop");
    }
  };

  const handleTouchStart = (e, action) => {
    e.preventDefault();
    if (action !== "stop") {
      sendCommand(action);
    }
  };

  const handleTouchEnd = (e, action) => {
    e.preventDefault();
    if (action !== "stop") {
      sendCommand("stop");
    }
  };

  const handleStopClick = () => {
    sendCommand("stop");
  };

  const renderControlPanel = () => (
    <div className="control-panel">
      <div className="video-section">
        <div className="video-container">
          {videoStream && isVideoConnected ? (
            <img 
              src={videoStream} 
              alt="Live Video Feed" 
              className="video-feed"
            />
          ) : (
            <div className="video-placeholder">
              <div className="placeholder-icon">üìπ</div>
              <p>{isVideoConnected ? 'Connecting to video stream...' : 'Live video feed will appear here'}</p>
              <small>{videoUrl ? `Stream URL: ${videoUrl}` : 'Configure video stream URL in settings'}</small>
              {!isVideoConnected && videoUrl && (
                <div className="video-status">
                  <span className="status-indicator">
                    <div className="status-dot disconnected"></div>
                    Video stream disconnected
                  </span>
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      <div className="controller">
        <div></div>
        <button 
          id="forward"
          onMouseDown={() => handleMouseDown("forward")}
          onMouseUp={() => handleMouseUp("forward")}
          onTouchStart={(e) => handleTouchStart(e, "forward")}
          onTouchEnd={(e) => handleTouchEnd(e, "forward")}
          disabled={!isConnected}
        >
          ‚¨Ü Forward
        </button>
        <div></div>

        <button 
          id="left"
          onMouseDown={() => handleMouseDown("left")}
          onMouseUp={() => handleMouseUp("left")}
          onTouchStart={(e) => handleTouchStart(e, "left")}
          onTouchEnd={(e) => handleTouchEnd(e, "left")}
          disabled={!isConnected}
        >
          ‚¨Ö Left
        </button>
        <button 
          id="stop"
          onClick={handleStopClick}
          disabled={!isConnected}
        >
          ‚èπ Stop
        </button>
        <button 
          id="right"
          onMouseDown={() => handleMouseDown("right")}
          onMouseUp={() => handleMouseUp("right")}
          onTouchStart={(e) => handleTouchStart(e, "right")}
          onTouchEnd={(e) => handleTouchEnd(e, "right")}
          disabled={!isConnected}
        >
          ‚û° Right
        </button>

        <div></div>
        <button 
          id="backward"
          onMouseDown={() => handleMouseDown("backward")}
          onMouseUp={() => handleMouseUp("backward")}
          onTouchStart={(e) => handleTouchStart(e, "backward")}
          onTouchEnd={(e) => handleTouchEnd(e, "backward")}
          disabled={!isConnected}
        >
          ‚¨á Backward
        </button>
        <div></div>
      </div>
    </div>
  );

  // Fix Leaflet default markers
  useEffect(() => {
    delete L.Icon.Default.prototype._getIconUrl;
    L.Icon.Default.mergeOptions({
      iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
      iconUrl: require('leaflet/dist/images/marker-icon.png'),
      shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
    });
  }, []);

  const renderAlerts = () => (
    <div className="alerts-panel">
      <h3>System Alerts & Location Map</h3>

      <div className="map-section">
        <MapContainer
          center={[40.7128, -74.0060]}
          zoom={12}
          style={{ height: '400px', width: '100%', borderRadius: '8px' }}
        >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
        </MapContainer>
      </div>

      <div className="alerts-list">
        <h4>Recent Alerts</h4>
        {alerts.map(alert => (
          <div key={alert.id} className="alert-item">
            <div className="alert-content">
              <span className="alert-message">{alert.message}</span>
            </div>
            <button className="alert-dismiss">√ó</button>
          </div>
        ))}
      </div>
    </div>
  );

  const renderGeofencing = () => (
    <div className="geofencing-panel">
      <h3>Geofencing Settings</h3>
      <div className="settings-form">
        <div className="form-group">
          <label>
            <input 
              type="checkbox" 
              checked={geofenceSettings.enabled}
              onChange={(e) => setGeofenceSettings({...geofenceSettings, enabled: e.target.checked})}
            />
            Enable Geofencing
          </label>
        </div>
        <div className="form-group">
          <label>Radius (meters):</label>
          <input 
            type="number" 
            value={geofenceSettings.radius}
            onChange={(e) => setGeofenceSettings({...geofenceSettings, radius: parseInt(e.target.value)})}
            disabled={!geofenceSettings.enabled}
          />
        </div>
        <div className="form-group">
          <label>Center Latitude:</label>
          <input 
            type="number" 
            value={geofenceSettings.center.lat}
            onChange={(e) => setGeofenceSettings({
              ...geofenceSettings, 
              center: {...geofenceSettings.center, lat: parseFloat(e.target.value)}
            })}
            disabled={!geofenceSettings.enabled}
          />
        </div>
        <div className="form-group">
          <label>Center Longitude:</label>
          <input 
            type="number" 
            value={geofenceSettings.center.lng}
            onChange={(e) => setGeofenceSettings({
              ...geofenceSettings, 
              center: {...geofenceSettings.center, lng: parseFloat(e.target.value)}
            })}
            disabled={!geofenceSettings.enabled}
          />
        </div>
        <button className="save-button">Save Settings</button>
      </div>
    </div>
  );

  const renderSettings = () => (
    <div className="settings-panel">
      <h3>System Settings</h3>
      <div className="settings-form">
        <div className="form-group">
          <label>Video Stream URL:</label>
          <input 
            type="text" 
            value={videoUrl}
            onChange={(e) => setVideoUrl(e.target.value)}
            placeholder="wss://your-video-server.com"
          />
          <small>WebSocket URL for live video stream (e.g., wss://live-video-serbot.onrender.com)</small>
        </div>
        <div className="form-group">
          <label>WebSocket URL:</label>
          <input 
            type="text" 
            defaultValue="wss://bot-controll.onrender.com/ws"
            placeholder="Enter WebSocket URL"
          />
        </div>
        <div className="form-group">
          <label>Connection Timeout (seconds):</label>
          <input 
            type="number" 
            defaultValue="30"
            placeholder="30"
          />
        </div>
        <button className="save-button">Save Settings</button>
      </div>
    </div>
  );

  return (
    <div className="App">
      <aside className="sidebar">
        <div className="sidebar-header">
          <h1>Vehicle Control Dashboard</h1>
        </div>

        <nav className="sidebar-nav">
          <button 
            className={`nav-item ${activeTab === 'control' ? 'active' : ''}`}
            onClick={() => setActiveTab('control')}
          >
            <span className="nav-icon">üéÆ</span>
            Control
          </button>
          <button 
            className={`nav-item ${activeTab === 'alerts' ? 'active' : ''}`}
            onClick={() => setActiveTab('alerts')}
          >
            <span className="nav-icon">üö®</span>
            Alerts
          </button>
          <button 
            className={`nav-item ${activeTab === 'geofencing' ? 'active' : ''}`}
            onClick={() => setActiveTab('geofencing')}
          >
            <span className="nav-icon">üìç</span>
            Geofencing
          </button>
          <button 
            className={`nav-item ${activeTab === 'settings' ? 'active' : ''}`}
            onClick={() => setActiveTab('settings')}
          >
            <span className="nav-icon">‚öôÔ∏è</span>
            Settings
          </button>
        </nav>

        <div className="sidebar-footer">
          <div className="status-indicator">
            <div className={`status-dot ${isConnected ? 'connected' : 'disconnected'}`}></div>
            <span className="status-text">{status}</span>
          </div>
        </div>
      </aside>

      <main className="main-content">
        {activeTab === 'control' && renderControlPanel()}
        {activeTab === 'alerts' && renderAlerts()}
        {activeTab === 'geofencing' && renderGeofencing()}
        {activeTab === 'settings' && renderSettings()}
      </main>
    </div>
  );
}

export default App;
